params.blog_listing_source:
{% set path = request.path|split('/', 2)|last %}
{% set query = request.query_dict.category %}

{% set type = request.query_dict.type %}
{% set text = request.query_dict.text %}

{% set total_pages = contents.total_page_count %}
{% set more_pages = total_pages - current_page_num %}

{% set techBlogId = path == 'technology' ? 54615482386 : (path == 'culture' ? 54780923926 : 54784757872)  %}
{% set tech_recent_posts = blog_recent_posts(techBlogId, 200) %}
{% set tech_popular_posts = blog_popular_posts(techBlogId, 200) %}

{% set blogTagClass = text == '기술' ? 'cate-tag-t' : (text == '사람&문화' ? 'cate-tag-c' : 'cate-tag-s') %}


{% set show = path == 'post' || path == 'culture' || path == 'sustainability' ? true : flase %}


{% if !show %}

<section class="list-contentsWrap">
	<div class="conWrap-body">
		<div class="inner2">
<!-- 			<p class='choiceBlog'></p> -->
			<ul class="flexWrap-4 addFlewWrap-4 recent-blog"> 
      {% for content in contents %}
				  <li class='flewWrap-li'>
						<div class="card">
							<div class="imgWrap">
								<a href="{{content.absolute_url}}"><img class=" ls-is-cached lazyloaded" src='{{content.featured_image ? content.featured_image : get_asset_url("/hynixNewsRoom/codeFiles/images/defaultImage/tnoimg.png")}}' alt="이미지"></a>
							</div>
							<div class="tagWrap">

									{% for topic in content.topic_list %}
									{% if topic == 'blog-기술'%}
										<a href="/post/tag/blog-기술/page/1?type=blog&text=기술">
											<div class="cate-tag cate-tag-t">기술</div>
										</a>
								  {% endif %}
								  {% endfor %}

								  {% for topic in content.topic_list %}
									{% if topic == 'blog-사람-문화'%}
										<a href="/post/tag/blog-사람-문화/page/1?type=blog&text=사람&문화">
											<div class="cate-tag cate-tag-c">사람&문화</div>
										</a>
								  {% endif %}
								  {% endfor %}

								  {% for topic in content.topic_list %}
									{% if topic == 'blog-지속가능경영'%}
								    <a href="/post/tag/blog-지속가능경영/page/1?type=blog&text=지속가능경영">
									    <div class="cate-tag cate-tag-s">지속가능경영</div>
								    </a>
									{% endif %}
									{% endfor %}
							</div>

							<a href="{{content.absolute_url}}">
								<h2>{{content.name}}</h2>
							</a>
						</div>
					</li>
				{% endfor %}
			</ul>
		</div>
	</div>
</section>
{% endif %}


{% if not simple_list_page %}

<div class="inner2 inner2-paginationWrap">
	{% if total_pages > 1 %}
		<nav class="skh-paginationWrap">
			<ul class="skh-pagination">

				{% if last_page_num %}
				<li class="page-prev-2">
					<a>
						<span>ic-prev</span>
					</a>
				</li>
				<li class="page-prev">
					<a href="{{ blog_page_link(last_page_num) }}?type={{type}}&text={{text}}">
						<span>이전<i></i></span>
					</a>
				</li>
				{% else %}
					<li class="page-prev-2" style='visibility: hidden'>
						<a>
							<span>ic-prev</span>
						</a>
					</li>
					<li class="page-prev" style='visibility: hidden'>
						<a href="{{ blog_page_link(last_page_num) }}?type={{type}}&text={{text}}">
							<span>이전<i></i></span>
						</a>
					</li>
				{% endif %}

				{% if next_page_num %}
				<li class="page-next">
					<a href="{{ blog_page_link(next_page_num) }}?type={{type}}&text={{text == '사람&문화' || text == '사람' || text == '사람%26문화' ? '사람%26문화' : text}}">
						<span><i></i>다음</span>
					</a>
				</li>
				<li class="page-next-2">
					<a>
						<span>ic-next</span>
					</a>
				</li>
				{% else %}
				<li class="page-next" style='visibility: hidden'>
					<a href="{{ blog_page_link(next_page_num) }}?type={{type}}&text={{text == '사람&문화' || text == '사람' || text == '사람%26문화' ? '사람%26문화' : text}}">
						<span><i></i>다음</span>
					</a>
				</li>
				<li class="page-next-2" style='visibility: hidden'>
					<a>
						<span>ic-next</span>
					</a>
				</li>
				{% endif %}


			</ul>
	</nav>
	{% endif %}
</div>

{% endif %}


<script
  src="https://code.jquery.com/jquery-1.12.4.js"
  integrity="sha256-Qw82+bXyGq6MydymqBxNPYTaUXXq7c8v3CwiYwLLNXU="
  crossorigin="anonymous"></script>

<script type='text/babel'>
	const searchParam = (key) => {
    return new URLSearchParams(location.search).get(key);
  };

	const pathname = location.pathname;

	const type = searchParam('type')
	const text = searchParam('text')
	const splitPath = pathname.split('/')
	const movingUrl = splitPath.slice(0, splitPath.length - 1).join('/')

	let COUNT = 28;
	let currentPage = {{current_page_num}};
	const TOTAL = {{ blog_total_post_count(blogId) }};
	const totalPageCount = {{total_pages}};

	console.log(currentPage, TOTAL, totalPageCount, '!!!!!!!')

	let windowWidth = window.innerWidth



	let max = windowWidth <= 765 ? Math.ceil(parseInt(currentPage)/5) * 5 : Math.ceil(parseInt(currentPage)/10) * 10;
	let min = windowWidth <= 765 ? max - 4 : max - 9;
	let realMax = max >= totalPageCount ? totalPageCount : max;

	const sendText = text === '사람&문화' || text === '사람' || text === '사람%26문화' ? '사람%26문화' : text
	let pageItems = '';

	for(let i = min; i <= realMax; i++) {
		const active = currentPage === i ? 'active' : ''
		pageItems += `<li class='num'><a class="${active}" href="${movingUrl}/${i}?type=${type}&text=${sendText}">${i}</a></li>`		
	}

	$('.inner2 .skh-paginationWrap .skh-pagination .page-prev').after(pageItems)
	$('.inner2 .skh-paginationWrap .skh-pagination .page-prev-2 a').attr('href', `${movingUrl}/1?type=${type}&text=${sendText}`)
	$('.inner2 .skh-paginationWrap .skh-pagination .page-next-2 a').attr('href', `${movingUrl}/${totalPageCount}?type=${type}&text=${sendText}`)

	if(type !== 'blog') {
		$('.inner2 .skh-paginationWrap .skh-pagination').css('width', '100%')
		$('.inner2 .skh-paginationWrap .skh-pagination').css('justify-content', 'space-between')
	} 

</script>
