{% set media_id = module.media_blog_field  %}
{% set media_recent_posts = blog_recent_posts(media_id, 200) %}
{% set media_popular_posts = blog_popular_posts(media_id, 200) %}
{% set type = request.query_dict.type %}
{% set text = request.query_dict.text %}
<!--     div .listItem active가 붙으면 선택완료 -->
{% set media_id = module.media_blog_field  %}
{% set media_recent_posts = blog_recent_posts(media_id, 200) %}
{% set media_popular_posts = blog_popular_posts(media_id, 200) %}
<!--     div .listItem active가 붙으면 선택완료 -->

<section class="container media_module media-list-contents">
  <div class="conWrap-body conWrap-body-recent">
    <div class="inner2">

			<div class="statusbarWrap" style='display:flex;align-items:center;margin-bottom:32px'>
        <div class="w3-light-grey" style='height:5px; width:100%'>
          <div class="w3-green" style="height:5px;width:0%"></div>
        </div>
        <div class='barText' style='color:#055b9a;font-weight:bold;font-size:12px;margin-left:20px;width:2%'>0%</div>
      </div>

      <!--     최신글 -->
      <div class="listWrap recent-blog test-listing">
        {% for content in contents %}
        <div class="listItem test{{loop.index}}">
          <div class="imgBx">
            <a class='thumb-background'>
              <div class="imgWrap">
                <span class="thumbPlus">선택완료</span>
                {% if content.widgets.module_1631532455283101.body.choice_field == 'video' %}
                <div class="img-type img-type-mov" data-url="{{content.widgets.module_1631532455283101.body.video_file}}"></div>
                <img src="{{content.featured_image ? content.featured_image : get_asset_url("/hynixNewsRoom/codeFiles/images/contentImage/groupO2x.png")}}" class=" ls-is-cached lazyloaded" alt="img">
                <span class="thumbCheck"></span>
                {% elif content.widgets.module_1631532455283101.body.choice_field == 'youtube' %}
								<div class="img-type img-type-youtube"></div>
                <img src="{{content.featured_image ? content.featured_image : get_asset_url("/hynixNewsRoom/codeFiles/images/contentImage/groupO2x.png")}}" class=" ls-is-cached lazyloaded" alt="img">
                {% else %}
                <div class="img-type img-type-pic" data-url="{{content.widgets.module_1631532455283101.body.image_file}}"></div>
                <img src="{{content.featured_image ? content.featured_image : get_asset_url("/hynixNewsRoom/codeFiles/images/contentImage/groupO2x.png")}}" class=" ls-is-cached lazyloaded" alt="img">
                <span class="thumbCheck"></span>
                {% endif %}
              </div>
            </a>
            {% if content.widgets.module_1631532455283101.body.choice_field == 'video' %}
            <a class='dataDown' data-data="{{content.widgets.module_1631532455283101.body.choice_field}}" data-img="{{content.widgets.module_1631532455283101.body.image_file ? content.widgets.module_1631532455283101.body.image_file : null}}" data-video="{{content.widgets.module_1631532455283101.body.video_file ? content.widgets.module_1631532455283101.body.video_file : null}}" data-youtube="{{content.widgets.module_1631532455283101.body.youtubeId ? content.widgets.module_1631532455283101.body.youtubeId : null}}" style="cursor:pointer" class="hoverInDownload"><i></i>다운로드</a>
            {% elif content.widgets.module_1631532455283101.body.choice_field == 'youtube' %}
            <a class='dataDown' data-data="{{content.widgets.module_1631532455283101.body.choice_field}}" data-img="{{content.widgets.module_1631532455283101.body.image_file ? content.widgets.module_1631532455283101.body.image_file : null}}" data-video="{{content.widgets.module_1631532455283101.body.video_file ? content.widgets.module_1631532455283101.body.video_file : null}}" data-youtube="{{content.widgets.module_1631532455283101.body.youtubeId ? content.widgets.module_1631532455283101.body.youtubeId : null}}" style="cursor:pointer" class="hoverInDownload"></a> 
            {% elif content.widgets.module_1631532455283101.body.choice_field == 'img' %}
            <a class='dataDown' data-data="{{content.widgets.module_1631532455283101.body.choice_field}}" data-img="{{content.widgets.module_1631532455283101.body.image_file ? content.widgets.module_1631532455283101.body.image_file : null}}" data-video="{{content.widgets.module_1631532455283101.body.video_file ? content.widgets.module_1631532455283101.body.video_file : null}}" data-youtube="{{content.widgets.module_1631532455283101.body.youtubeId ? content.widgets.module_1631532455283101.body.youtubeId : null}}" style="cursor:pointer" class="hoverInDownload"><i></i>다운로드</a>
            {% endif %}

          </div>
          <a href="{{content.absolute_url}}">
            <h3>{{content.name}}</h3>
          </a>
          <hr>
          <div class="ml_con_info">
            <div class="info">
<!--               <span class="date">
                {{content.publish_date | datetimeformat ('%Y-%m-%d')}}
              </span> -->
              <span id='name1{{loop.index}}' class="name">
                {% for topic in content.topic_list %}
                {% if topic != '' %}
                <a href='/library/tag/{{encodeURIComponent(topic.name)}}/page/1' class='topicText topicText1{{loop.index}}'>#{{topic.name}}</a>
                {% else %}
                {% endif%}
                {% endfor %}
              </span>
            </div>
          </div>
        </div>        
        {% endfor %}
      </div>
    </div>
    <a id='loady' class="card-more recent-blog-btn recent-blog-btn2"> 
      더보기
      <i></i>
    </a>
  </div>
  <!-- 	<a href="#" class="card-more">
더보기
<i></i>
</a> -->
</section>

{% if not simple_list_page %}
<div class="inner2 inner2-paginationWrap">
  <nav class="skh-paginationWrap">
    <ul class="skh-pagination" style="width:100%; justify-content:space-between">

			{% if last_page_num %}
			<li class="page-prev-2">
				<a>
					<span>ic-prev</span>
				</a>
			</li>
			<li class="page-prev">
				<a href="{{ blog_page_link(last_page_num) }}?type={{type}}&text={{text}}">
					<span>이전<i></i></span>
				</a>
			</li>
			{% else %}
			<li class="page-prev-2" style='visibility: hidden'>
				<a>
					<span>ic-prev</span>
				</a>
			</li>
			<li class="page-prev" style='visibility: hidden'>
				<a href="{{ blog_page_link(last_page_num) }}?type={{type}}&text={{text}}">
					<span>이전<i></i></span>
				</a>
			</li>
			{% endif %}


<!--       <li class="page-prev-2" style='visibility: hidden;'>
        <a>
          <span>ic-prev</span>
        </a>
      </li> -->


			{% if next_page_num %}
			<li class="page-next">
				<a href="{{ blog_page_link(next_page_num) }}?type={{type}}&text={{text}}">
					<span><i></i>다음</span>
				</a>
			</li>
			<li class="page-next-2">
				<a>
					<span>ic-next</span>
				</a>
			</li>
			{% else %}
			<li class="page-next" style='visibility: hidden'>
				<a href="{{ blog_page_link(next_page_num) }}?type={{type}}&text={{text}}">
					<span><i></i>다음</span>
				</a>
			</li>
			<li class="page-next-2" style='visibility: hidden'>
				<a>
					<span>ic-next</span>
				</a>
			</li>
			{% endif %}

    </ul>
  </nav>
</div>
{% endif %}

